import React from "react";
import { useSelector } from "react-redux";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    marginBottom: '1rem',
  },
  media: {
    width: 200,
    height: 200,
    backgroundSize: 'contain',
    margin: '1rem',
  },
  content: {
    flex: '1 0 auto',
  },
  meta: {
    color: theme.palette.text.secondary,
  },
  header: {
    fontWeight: 'bold',
    marginBottom: '0.5rem',
  },
  description: {
    marginTop: '0.5rem',
  },
}));

const ProductComponent = () => {
    const products = useSelector((state) => state.allProducts.products);
    const renderList = products.map((product) => {
      const { _id, product_name, product_id, product_description, brands } = product;
      return(
        <div className="">
          <div className="stackable four column wide" key={ _id }>
            <div className="ui cards">
              <div className="card">
                  <div className="image">
                      <img src={ brands } alt={ product_name } />
                  </div>
                  <div className="content">
                      <div className="meta">{ product_id }</div>
                      <div className="header">{ product_name }</div>
                      <div className="description">{ product_description }</div>
                  </div>
              </div>
            </div>
          </div>
        </div>
  );
    })
    return (
      <>{ renderList }</>
    )
};

export default ProductComponent;
