import React from "react";
import { useSelector } from "react-redux";

const ProductComponent = () => {
    const products = useSelector((state) => state.allProducts.products);
    const renderList = products.map((product) => {
      const { product_name, product_id, product_description, brands } = product;
      const brandImages = brands.map((brand) => brand.Brand_image);
      return(
        <div className="four column wide">
          <div className="ui link cards">
              <div className="card">
                  <div className="image">
                      <img src={brandImages[0]} alt={product_name} />
                  </div>
                  <div className="content">
                      <div className="meta">{ product_id }</div>
                      <div className="header">{ product_name }</div>
                      <div className="meta">{ product_description }</div>
                  </div>
              </div>
          </div>
      </div>);
    })
    return (
      <>{ renderList }</>
    )
};

export default ProductComponent;
