import React from "react";
import { useSelector } from "react-redux";

const ProductComponent = () => {
    const products = useSelector((state) => state.allProducts.products);
    const renderList = products.map((product) => {
      const { _id, product_name, product_id, product_description, brands : {Brand_image} } = product;
      
      return(
        <div className="">
          <div className="stackable four column wide">
            <div className="ui cards">
              <div className="card">
                  <div className="image">
                      <img src={ Brand_image } alt={ product_name } />
                  </div>
                  <div className="content">
                      <div className="meta">{ product_id }</div>
                      <div className="header">{ product_name }</div>
                      <div className="description">{ product_description }</div>
                  </div>
              </div>
            </div>
          </div>
        </div>
  );
    })
    return (
      <>{ renderList }</>
    )
};

export default ProductComponent;
